import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { CategoryList } from '../header/category/CategoryList'


export default class Header extends Component {
    render() {
        const { match } = this.props;
        let rootPath = '';
        const foundedCategory = CategoryList.find(category => category.path === match?.path);
        if (match) {
            rootPath = match.path.split('/')[1];
        }
        return (

            <div class="ui  inverted menu">
                <a class="active item">
                    {
                        CategoryList.filter(category => category.categoryType === rootPath).map(category => (
                            <Link to={category.path} class="item"
                                key={`CATEGORY_LINK_${category.categoryId}`}>
                                {category.categoryName}
                            </Link>
                        ))}
                </a>

                <div class="right menu">
                    <div class="item">
                        <div class="ui primary button">Giriş Yap</div>
                    </div>
                    <div className="item">
                        <a className="item">
                            <Link to="/contact">
                                İletişim
                            </Link>
                        </a>
                    </div>
                </div>
            </div>
        )
    }

}
